import React, { useState, useEffect, useCallback } from "react";

import MoviesList from "./components/MoviesList";
import "./App.css";

function App() {
  const [movies, setMovies] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  const [errorTry, setErrorTry] = useState(true);

  const fetchMovieHandler = useCallback(async () => {
    try {
      const response = await fetch("https://swapi.dev/api/film");

      if (!response.ok) {
        throw new Error("Something wents wrong......Retrying");
      }

      const data = await response.json();
      setIsLoading(true);
      setError(null);
      const transformedMovies = data.results.map((movieData) => {
        return {
          id: movieData.episode_id,
          title: movieData.title,
          openingText: movieData.opening_crawl,
          releaseDate: movieData.release_date,
        };
      });
      setMovies(transformedMovies);
    } catch (error) {
      if (errorTry) {
        setInterval(() => {
          setError(error.message);
          console.log(error.message);
        }, 5000);
      }
    }
    setIsLoading(false);
  }, [errorTry]);

  useEffect(() => {
    fetchMovieHandler();
  }, [fetchMovieHandler]);

  const errorTryHandler = () => {
    setErrorTry((prev) => !prev);
  };

  return (
    <React.Fragment>
      <section>
        {!error && <button onClick={fetchMovieHandler}>Fetch Movies</button>}
        {error && <button onClick={errorTryHandler}>Cancel</button>}
      </section>
      <section>
        {!isLoading && movies.length > 0 && <MoviesList movies={movies} />}
        {!isLoading && movies.length === 0 && !error && (
          <p>There is no movie found..</p>
        )}
        {!isLoading && error && <p>{error}</p>}
        {isLoading && <p>loading....</p>}
      </section>
    </React.Fragment>
  );
}

export default App;
